void FlowPlayThumb_DrawDateTime(void)
{
	UINT32  CurrFileIndex;	//index in thumb
	//BOOL    bLock;
	UINT16 PBAttrib;
	UINT32 uiFileNum = 0;
	PFILEDB_FILE_ATTR  pFileAttr = NULL;;
	UINT32  *pThumbSeqID;
	UINT32 CurrFileSeqID;
	UINT16  *pCurrFileFormat;   // Hideo@0503: variable type should be UINT16
	UINT32 uiParamVal;
	UINT16 TempLastWriteTime;                        ///< The latest time of write file.
	UINT16 TempLastWriteDate;                        ///< The latest date of write file.
	UINT32  /*creDateTime[6],*/modDateTime[6];
	static char item1_Buf[34];
	static char item2_Buf[34];
        UINT32 thousand,hundred;
	PB_GetParam(PBPRMID_TOTAL_FILE_COUNT, &uiFileNum);
       // MY_MSG("=====>uiFileNum %d \r\n",uiFileNum);
	if (uiFileNum)
	{
	     PB_GetParam(PBPRMID_THUMB_FMT_ARRAY, &uiParamVal);
	     pCurrFileFormat = (UINT16 *)uiParamVal;

	     PB_GetParam(PBPRMID_THUMB_CURR_IDX, &CurrFileIndex);
		 
	     PBAttrib = *(pCurrFileFormat+CurrFileIndex-1);
		 
	     FileSys_WaitFinish();
	     PB_GetParam(PBPRMID_THUMB_SEQ_ARRAY, &uiParamVal);
	     pThumbSeqID = (UINT32 *)uiParamVal;	
		 
	     pThumbSeqID += (CurrFileIndex-1);
	     CurrFileSeqID = *pThumbSeqID;


		pFileAttr = FileDB_SearhFile2(0,(CurrFileSeqID-1));

	//
	///< File created time. bit0~4: seconds/2, bit5~10: minutes, bit11~15: hours.
	///< File created date. bit0~4: day(1-31), bit5~8: month(1-12), bit9~15: year(0-127) add 1980 to convert
		TempLastWriteDate = pFileAttr->creDate;
		TempLastWriteTime =pFileAttr->creTime;

		//debug_err(("bobo FlowThumb_DateTime  0x%x  , 0x%x \r\n",TempLastWriteDate,TempLastWriteTime));

		modDateTime[0]=((TempLastWriteDate>>9)&0x007F)+1980;	
		modDateTime[1]=(TempLastWriteDate>>5)&0x000F;
		modDateTime[2]=TempLastWriteDate&0x001f;
		//
		modDateTime[3]=(TempLastWriteTime>>11)&0x001F;	
		modDateTime[4]=(TempLastWriteTime>>5)&0x003F; 
	//date
#if 1// miotone_2018829 zejia change
                //MY_MSG("=====>modDateTime0 %d \r\n",modDateTime[0]);
                //modDateTime[0] = 2151;//test
                thousand = modDateTime[0] / 1000;
                hundred = (modDateTime[0] % 1000) / 100;
                modDateTime[0] = modDateTime[0] -(1000 * thousand) - (100 * hundred);
                //MY_MSG("=====>thousand %d hundred %d modDateTime0 %d \r\n",thousand,hundred,modDateTime[0]);
	        if (modDateTime[0] > 50)  modDateTime[0] = 50;
#else
	    //modDateTime[0] %= 100;
	    modDateTime[0] %= 10000;

	    //if (modDateTime[0] > 50)  modDateTime[0] = 50;
	    if ((modDateTime[0]%100) > 50)  modDateTime[0] = 50;
#endif        
	    if (modDateTime[1] > 12)  modDateTime[0] = 12;
	    if (modDateTime[2] > 31)  modDateTime[0] = 31;
	    snprintf(item1_Buf,20,"%02lu/%02lu/%02lu",modDateTime[0],modDateTime[1],modDateTime[2]);

	//time
	    if (modDateTime[3]>23)   modDateTime[3]=0;
	    if (modDateTime[4]>59)   modDateTime[4]=0;
	    snprintf(item2_Buf,20,"%02lu:%02lu",modDateTime[3],modDateTime[4]);

	    snprintf(ModYMDStr,20,"%s",item1_Buf);
           snprintf(ModDateTimeStr,20,"%s",item2_Buf);
		   
	    UxStatic_SetData(&UIFlowWndPlayThumb_Static_YMDCtrl,STATIC_VALUE,Txt_Pointer(item1_Buf));
	    UxStatic_SetData(&UIFlowWndPlayThumb_Static_HMSCtrl,STATIC_VALUE,Txt_Pointer(item2_Buf));
	    UxCtrl_SetShow(&UIFlowWndPlayThumb_Static_YMDCtrl,TRUE);
	    UxCtrl_SetShow(&UIFlowWndPlayThumb_Static_HMSCtrl,TRUE);
	}
}
INT32 UIFlowWndPlayThumb_OnOpen(VControl *pCtrl, UINT32 paramNum, UINT32 *paramArray)
{
	PURECT          pThumbRect;
	PLAY_BROWSER_OBJ     FlowPBBrowserObj = {0};
	UINT32 FileNumsInDir = 0, NumsPerPage;
	UINT8   HorNums, VerNums;
	UINT32 uiStatus;
    CHKPNT;
	FlowPB_ClearAllThumbIcon();
	pThumbRect = FlowPB_ThumbDisplay();
	HorNums = FLOWPB_THUMB_H_NUM2;
	VerNums = FLOWPB_THUMB_V_NUM2;
	PB_SetParam(PBPRMID_THUMB_LAYOUT_ARRAY, (UINT32)pThumbRect);
//#NT#2012/11/27#Scottie -begin
//#NT#Support Dual Display for PB
	if (System_GetEnableDispCount() == 2) {
		PB_SetParam(PBPRMID_THUMB_LAYOUT_ARRAY2, (UINT32)pThumbRect);
	}
//#NT#2012/11/27#Scottie -end
	NumsPerPage = VerNums * HorNums;
	debug_msg("-------UIFlowWndPlayThumb_OnOpen------NumsPerPage:%d  VerNums:%d  ", NumsPerPage, VerNums);
	PB_GetParam(PBPRMID_TOTAL_FILE_COUNT, &FileNumsInDir);
	FlowPBBrowserObj.BrowserCommand = PB_BROWSER_CURR;
	FlowPBBrowserObj.HorNums        = HorNums;
	FlowPBBrowserObj.VerNums        = VerNums;
	FlowPBBrowserObj.slideEffectFunc = PBX_SLIDE_EFFECT_NONE;
	debug_msg("FileNumsInDir:%d NumsPerPage: %d \r\n",FileNumsInDir, NumsPerPage );
	if (FileNumsInDir <= NumsPerPage) { // means only one page
		FlowPBBrowserObj.bReDecodeImages = FALSE;
	} else {
		FlowPBBrowserObj.bReDecodeImages = TRUE;
	}
	PB_PlayBrowserMode(&FlowPBBrowserObj);
	uiStatus = PB_WaitCommandFinish(PB_WAIT_INFINITE);
	if (uiStatus & PB_STA_NOIMAGE) // 没有文件
	{
		Ux_OpenWindow(&UIFlowWndWrnMsgCtrl, 2, FLOWWRNMSG_ISSUE_NO_IMAGE, FLOWWRNMSG_TIMER_2SEC);
		g_bUIFlowWndPlayNoImgWndOpened = TRUE;
		Ux_CloseWindow(&UIFlowWndPlayThumbCtrl,0);
		return NVTEVT_CONSUME;

	}
	if ( UIPlay_GetRootDirType() == ROOTDIR_TYPE_PHOTO)
	{
		UxStatic_SetData(&UIFlowWndPlayThumb_Static_TitleCtrl,STATIC_VALUE,STRID_PHOTOALBUM);

	}else if ( UIPlay_GetRootDirType() == ROOTDIR_TYPE_MOVIE_EMR)
	{
		UxStatic_SetData(&UIFlowWndPlayThumb_Static_TitleCtrl,STATIC_VALUE,STRID_PLAYBACK_URG);

	}else if ( UIPlay_GetRootDirType() == ROOTDIR_TYPE_MOVIE)
	{
		UxStatic_SetData(&UIFlowWndPlayThumb_Static_TitleCtrl,STATIC_VALUE,STRID_PLAYBACK_LOOP);

	}
	FlowPB_ShowAllThumbIcon(); // 显示图标
	//	FlowPB_IconDrawThumbSeq(TRUE);
	FlowPlayThumb_DrawDateTime();

	Ux_DefaultEvent(pCtrl, NVTEVT_OPEN_WINDOW, paramNum, paramArray);
	return NVTEVT_CONSUME;
}
