{
	Ux_PostEvent(NVTEVT_KEY_ENTER, 1, NVTEVT_KEY_PRESS);
		    return NVTEVT_CONSUME;
			if ( bDetClick == 0)
	{
		UIPlay_PlaySingle(PB_SINGLE_CURR);
		UIFlowWndPlay_CheckStatus();
	}else{
		bDetClick = 0;
	}
	Ux_CloseWindow((VControl *)(&UIFlowWndPlayThumbCtrl), 1, NVTRET_THUMBNAIL);


    return NVTEVT_CONSUME;
}
